import { Injectable } from '@angular/core';
import {
  HttpRequest,
  HttpHandler,
  HttpInterceptor,
} from '@angular/common/http';

import { Router } from '@angular/router';
import { Observable, Subject, catchError, throwError } from 'rxjs';
import { TokenStorageService } from '@services/token-storage.service';

@Injectable()
export class TokenInterceptor implements HttpInterceptor {
  refreshTokenInProgress = false;
  tokenRefreshedSource = new Subject();
  tokenRefreshed$ = this.tokenRefreshedSource.asObservable();

  constructor(
    private router: Router,
    private tokenStorageService: TokenStorageService,
) { }

  addAuthHeader(request) {
   
    const authHeader = this.tokenStorageService.getToken();
    // const authHeader = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..FbK_FtaGhfuux8_84cIgs0v2O89wfOnXvWEDKmHTGMg';
    if (authHeader) {
      return request.clone({
        setHeaders: {
          'Authorization': `Bearer ` + authHeader
        }
      });
    }
    return request;
  }

  async handleResponseError(error, request?, next?) {
    // Business error
    if (error.status === 400) {
      const errMessage = await (new Response(error.error)).text();
    }

    return throwError(error);
  }

  intercept(request: HttpRequest<any>, next: HttpHandler): Observable<any> {
    // Handle request
    request = this.addAuthHeader(request);

    // Handle response
    return next.handle(request).pipe(catchError(error => {
      return this.handleResponseError(error, request, next);
    }));
  }
}
